import React, { useState } from "react";

const Button = (props) => <button onClick={props.incrementFeedback}>{props.text}</button>

const Statistics = ({good, neutral, bad}) => {
  return (
  <div>
    <p>Good {good}</p>
    <p>Neutral {neutral}</p>
    <p>Bad {bad}</p>
  </div>
  )
}

const App = () => {
  const [feedback, setFeedback] = useState({
    good: 0, neutral: 0, bad: 0
  })

  const incrementFeedback = (type) => {
        const newFeedback = {
        ...feedback,
        type: feedback.type + 1
      }
      setFeedback(newFeedback)
  }

  return (
    <div>
      <h1>Give feedback</h1>
      <Button handleClick={incrementFeedback(good)} text='Good' />
      <Button handleClick={incrementFeedback(neutral)} text='Neutral' />
      <Button handleClick={incrementFeedback(bad)} text='Bad' />
      <h1>Statistics</h1>
      <Statistics good={feedback.good} neutral={feedback.neutral} bad={feedback.bad} />   
    </div>
  )

}



export default App;
