import React, { useState } from "react";

const App = () => {
  const [feedback, setFeedback] = useState({
    good: 0, neutral: 0, bad: 0
  })

  const handleClick = type => {
    if (type === 'good') {
      const newFeedback = {
        ...feedback,
        good: good + 1
      }
      setFeedback(newFeedback)
    }
    if (type === 'neutral') {
      const newFeedback = {
        ...feedback,
        neutral: neutral + 1
      }
      setFeedback(newFeedback)
    }
    if (type === 'bad') {
      const newFeedback = {
        ...feedback,
        bad: bad + 1
      }
      setFeedback(newFeedback)
    }
  }

}



export default App;
