import React, { useState } from "react";

const Part = (props) => {
  return (
    <div>
      <p>{props.courseName} {props.numOfExercises}</p>
    </div>
  )
}

const Header = (props) => {
  return (
    <div>
      <p>{props.courseName}</p>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part courseName={props.courseNames[0]} numOfExercises={props.numOfExercises[0]} />
      <Part courseName={props.courseNames[1]} numOfExercises={props.numOfExercises[1]} />
      <Part courseName={props.courseNames[2]} numOfExercises={props.numOfExercises[2]} />
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>{props.total}</p>
    </div>
  )
}

const Display = ({counter}) => <div>{counter}</div>;
const Button = ({handleClick, text}) => (
  <button onClick={handleClick}>
    {text}
  </button>
)
const History = (props) => {
  if (props.allClicks.length === 0) {
    return (
      <div>
        the app is used by clicking the buttons
      </div>
    )
  }
  return (
    <div>
      button press history: {props.allClicks.join(' ')}
    </div>
  )
}


const App = () => {
  const [left, setLeft] = useState(0)
  const [right, setRight] = useState(0)
  const [allClicks, setAll] = useState([])

  const handleLeftClick = () => {
    setAll(allClicks.concat('L'))
    setLeft(left + 1)
  }

  const handleRightClick = () =>{
    setAll(allClicks.concat('R'))
    setRight(right + 1)
  }



  return (
    <div>
      {left}
      <button onClick={handleLeftClick}>left</button>
      <button onClick={handleRightClick}>right</button>
      {right}
      <History allClicks={allClicks} />
    </div>
  )
}



export default App;
